var chalk = require('chalk');
var assets_conf = require('../gulp/config').assets;

var  assets = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("assets")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

assets.usage = [
    'The ' + chalk.magenta('`base assets`') + 'copy the contents of "./' + assets_conf.src + '" in to public folder.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base assets'
];

module.exports = assets;