var chalk = require('chalk');

var  build = function (cb) {

	var gtask = require('../tasks/gtask');

	gtask("build")
	//.catch is ommited, errors handling is on cli
	.then(function () {
		cb(null);
	});
};

build.usage = [
	'The ' + chalk.magenta('`base build`') + ' command allows you build assets for development or production',
	'',
	'Usage:'.cyan.bold.underline,
	'',
	' base build'
];

module.exports = build;