var chalk = require('chalk');

var  clean = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("clean")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

clean.usage = [
    'The ' + chalk.magenta('`base clean`') + ' command clear public folder.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base clean'
];

module.exports = clean;