var chalk = require('chalk');

var  jade = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("jade")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

jade.usage = [
    'The ' + chalk.magenta('`base jade`') + ' command allows you to compile Jade files..',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base jade'
];

module.exports = jade;