var chalk = require('chalk');
var jsconf = require('../gulp/config').vendor.js;

var  libs = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("libs")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

libs.usage = [
    'The ' + chalk.magenta('`base libs`') + ' command join js files from bower and vendor, generates ./' + jsconf.dest + jsconf.outputName + '.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base libs'
];

module.exports = libs;