var chalk = require('chalk');
var ts = require('../gulp/config').typescript;

var  scripts = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("scripts")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

scripts.usage = [
    'The ' + chalk.magenta('`base scripts`') + ' command allows you to compile TypeScript files, generates ./' + ts.dest + ts.outputName + '.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base scripts'
];

module.exports = scripts;