var chalk = require('chalk');

var  serve = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("serve")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

serve.usage = [
    'The ' + chalk.magenta('`base serve`') + ' launchs your default browser and start watching souce files, fires live reload.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base serve'
];

module.exports = serve;