var chalk = require('chalk');
var vendorConfig = require('../gulp/config').vendor;
var stylesConfig = require('../gulp/config').styles;

var  styles = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("styles")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

styles.usage = [
    'The ' + chalk.magenta('`base styles`') + ' command wraps all CSS files imported on ' + vendorConfig.styles.src + ' and then compile SASS files, generates ' + stylesConfig.dest + stylesConfig.outputName + '.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base styles'
];

module.exports = styles;