var chalk = require('chalk');
var templatesConf = require('../gulp/config').templates;

var  templates = function (cb) {

    var gtask = require('../tasks/gtask');

    gtask("templates")
    //.catch is ommited, error handling is captured on main class.
    .then(function () {
        cb(null);
    });
};

templates.usage = [
    'The ' + chalk.magenta('`base templates`') + ' command allows you to pre-complile handlebars templates, generates ./' + templatesConf.dest + templatesConf.outputName + '.',
    '',
    'Usage:'.cyan.bold.underline,
    '',
    ' base templates'
];

module.exports = templates;