/**
 * base-app.js
 * Created by raul on 11/20/14.
 *
 * ===========
 * To add a new task, simply add a new task file that directory.
 * run `gulp help || gulp` to see all available tasks.
 */
function BaseApp(gulp) {
    this.gulp = gulp || require("gulp");
    this.init = function () {

        var requireDir = require('require-dir');
        // Require all tasks in gulp/tasks, including subfolders
        var gTasks = requireDir('./tasks', { recurse: true });
        gTasks.assets(this.gulp);
        gTasks.browserSync(this.gulp);
        gTasks.build(this.gulp);
        gTasks.clean(this.gulp);
        gTasks.jade(this.gulp);
        gTasks.libs(this.gulp);
        gTasks.scripts(this.gulp);
        gTasks.serve(this.gulp);
        gTasks.styles(this.gulp);
        gTasks.templates(this.gulp);
        gTasks.watch(this.gulp);

        return this;
    }
}

module.exports = BaseApp;

