var plugins = require('gulp-load-plugins')({scope: ['dependencies']});

module.exports = function (gulp) {
	var runSequence = require('run-sequence').use(gulp);
	gulp.task('build', function(callback) {
		runSequence('assets',
			'libs',
			'scripts',
			'styles',
			'jade',
			callback);
	});
}