var plugins = require('gulp-load-plugins')({scope: ['dependencies'],  pattern: ['gulp-*','del']});
var dest = require('../config').dest;

module.exports = function (gulp) {

  gulp.task('clean', function (cb) {
    plugins.del([dest + '**/*'], cb);
  });
}
